      text-align: left;
      padding: 0;
      margin: 1rem 0;
    }

    #contact p {
      text-align: center;
      margin: auto;
    }

    .screenshot-wrapper {
      img {
        width: 100%;
      }
    }
    h3.subtitle {
      text-align: left;
      margin-top: 3rem;
    }
    h3.subtitle::after {
      display: block;
      content: "";
      background-color: #ddd;
      margin: 0.75rem 0 1.5rem 0;
      height: 3px;
      width: 90%;
    }

    .gallery {
      box-sizing: border-box;

      .slider-for::after {
        content: "";
        display: block;
        background-color: #555;
        width: 20%;
        height: 5px;
        margin: 25px auto;
      }

      .slider-nav {
        box-sizing: border-box;
        img {
          margin: 0 0.5rem;
          cursor: pointer;
          box-sizing: border-box;
          border: 2px solid grey;
          transition: border-color 1s;

          &.slick-current {
            border-color: #800000;
          }
        }
      }
    }
    div.button-wrapper {
      width: 100%;
      text-align: center;
      height: 150px;

      a.visit-website {
        border: 2px solid #4b6cc1;
        display: inline-block;
        cursor: pointer;
        border-radius: 100px;
        background-color: #4b6cc1;
        font-size: 1.25rem;
        font-weight: 600;
        color: #fff;
        margin: 50px auto;
        padding: 1.5rem 2rem;
        text-decoration: none;
        transition: 0.3s;

        &:hover {
          transform: scale(1.1);
          background-color: #fff;
          color: #4b6cc1;
          border: 2px solid #4b6cc1;
        }
      }
    }
  }

  @media screen and (min-width: $tablet) {
    nav#project-nav {
      bottom: 50%;
      img {
        opacity: 0.8;
        height: 40px;
      }
    }
    .content {
      max-width: 1200px;
      margin: auto;

      p {
        max-width: 800px;
      }
    }
  }
}

